import React, { useEffect, useState } from 'react';
import './Received.css';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Received = () => {
  const [isSidebarOpen, setSidebarOpen] = useState(false);
  const [applications, setApplications] = useState([]);

  const toggleSidebar = () => {
    setSidebarOpen(!isSidebarOpen);
  };

  // Fetch applications from backend
  useEffect(() => {
    const fetchApplications = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/applications');
        setApplications(response.data);
      } catch (error) {
        console.error("Failed to fetch applications:", error);
      }
    };

    fetchApplications();
  }, []);

  return (
    <div className="main-content">
      <button className="hamburger" onClick={toggleSidebar}>☰</button>

      <aside className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
        <ul>
          <li><Link to="/job-creation"><b>Job Creation</b></Link></li>
          <li><Link to="/job-modification">Job Modification</Link></li>
          <li className="active"><Link to="/received-cvs">Received CVs</Link></li>
          <li><Link to="/accepted-cvs">Accepted CVs</Link></li>
        </ul>
      </aside>

      <section className="content">
        <div className="Jobcard">
          <h2>Received CVs</h2>
          <table>
            <thead>
              <tr>
                <th>Job Title</th>
                <th>Name</th>
                <th>Gender</th>
                <th>Field</th>
                <th>Contact Number</th>
                <th>CV</th>
              </tr>
            </thead>
            <tbody>
              {applications.map((app) => (
                <tr key={app._id}>
                  <td>{app.jobTitle}</td>
                  <td>{app.name}</td>
                  <td>{app.gender}</td>
                  <td>{app.field}</td>
                  <td>{app.contactNumber}</td>
                  <td>
                    {app.cv ? (
                      <a href={`http://localhost:5000/uploads/${app.cv}`} target="_blank" rel="noreferrer">
                        {app.cv}
                      </a>
                    ) : (
                      'No CV'
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>
    </div>
  );
};

export default Received;
